--
-- Table structure for table Poems
--

DROP TABLE IF EXISTS Writings;
CREATE TABLE Writings (
  ID Serial NOT NULL,
  TYPE varchar (255) NOT NULL,
  TITLE varchar(255),
  AUTHOR varchar(255)NOT NULL,
  YEAR int NOT NULL,
  TEXT text NOT NULL,
  PRIMARY KEY (ID)
);



;

DROP TABLE IF EXISTS User_Profiles;
CREATE TABLE User_Profiles (
  ID serial NOT NULL,
  Username char(55) NOT NULL,
  Full_Name char(55) NOT NULL,
  DOB char(55) NOT NULL,
  Badges int NOT NULL,
  PRIMARY KEY (ID)
);


